//Define Groovy Utils and holder for validating the XML reponse content
def groovyUtils = new com.eviware.soapui.support.GroovyUtils(context)
def holder = groovyUtils.getXmlHolder(messageExchange.responseContent)

//Define the NameSpace
holder.namespaces["ns1"] = "http://thomas-bayer.com/blz/"

//Get the Value of the Node 'bezeichnung' and assign to a variable
def BankName = holder.getNodeValue("//ns1:bezeichnung")

//Get the Value of the Node 'bic' and assign to a variable
def Bic = holder.getNodeValue("//ns1:bic")

//Get the Value of the Node 'ort' and assign to a variable
def ort = holder.getNodeValue("//ns1:ort")

//Get the Value of the Node 'plz' and assign to a variable
def plz = holder.getNodeValue("//ns1:plz")

//print the value of the result in the Output panel
log.info "The bank name is " + BankName

//Comparing the value to print 'Pass' or 'Fail'
if(BankName=="Berlin-Hannoversche Hypothekenbank")
{ log.info "Pass" }
else
{ log.info "fail"}

//print the value of the result in the Output panel
log.info "The bic is " + Bic

//Comparing the value to print 'Pass' or 'Fail'
if(Bic=="BHYPDEB1XXX")
{ log.info "Pass" }
else
{ log.info "fail"}

//print the value of the result in the Output panel
log.info "The ort is " + ort

//Comparing the value to print 'Pass' or 'Fail'
if(ort=="Berlin")
{ log.info "Pass" }
else
{ log.info "fail"}

//print the value of the result in the Output panel
log.info "The plz is " + plz

//Comparing the value to print 'Pass' or 'Fail'
if(plz=="10773")
{ log.info "Pass" }
else
{ log.info "fail"}

